CASE
WHEN <refobj>[Database Layer].[PART].[PARTTYPE]</refobj>   =&apos;DSW&apos; AND  <refobj>[Database Layer].[COMPONENT].[COMPONENTPN_7]</refobj>  =&apos;&apos;
THEN <refobj>[Database Layer].[PART].[PRODID_7]</refobj>  
WHEN <refobj>[Database Layer].[PART].[PARTTYPE]</refobj>   =&apos;DSW&apos; AND  <refobj>[Database Layer].[COMPONENT].[COMPONENTPN_7]</refobj>  &lt;&gt;&apos;&apos;
THEN   <refobj>[Database Layer].[COMPONENT].[COMPONENTPN_7]</refobj> 
WHEN<refobj>[Database Layer].[PART].[PARTTYPE]</refobj>    =&apos;ESW&apos; 
THEN <refobj>[Database Layer].[PART].[PRODID_7]</refobj>  
ELSE &apos;ERROR&apos;
END

CASE
WHEN <refobj>[Database Layer].[PART].[PARTTYPE]</refobj>   =&apos;DSW&apos; AND  <refobj>[Database Layer].[COMPONENT].[COMPONENTPN_7]</refobj>  =&apos;&apos;
THEN <refobj>[Database Layer].[PART].[PRODID_7]</refobj>  
WHEN <refobj>[Database Layer].[PART].[PARTTYPE]</refobj>   =&apos;DSW&apos; AND  <refobj>[Database Layer].[COMPONENT].[COMPONENTPN_7]</refobj>  &lt;&gt;&apos;&apos;
THEN   <refobj>[Database Layer].[COMPONENT].[COMPONENTPN_7]</refobj> 
WHEN<refobj>[Database Layer].[PART].[PARTTYPE]</refobj>    =&apos;ESW&apos; 
THEN <refobj>[Database Layer].[PART].[PRODID_7]</refobj>  
ELSE &apos;ERROR&apos;
END

