case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  &lt;&gt;0 and <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj>&lt;&gt;0)  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  &lt;&gt;0 and <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj>&lt;&gt;0)  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  not in (0,null) and <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj>&lt;&gt;0)  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  not in (0,null) and <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj> not in (0,null))  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  not in (0,null) and <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj> not in (0,null))  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or (  <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj> not in (0,null))  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  not in (0,null) )  then &apos;N&apos; 
else &apos;Y&apos;
end

case
when  <refobj>[Database Layer].[PAYMENTFACT].[RATEDETAILSKEY]</refobj>   = -1 
or ( <refobj>[Modelling Layer].[Payments].[FIXED_VOL_RATE]</refobj>  not in (0,null) and <refobj>[Modelling Layer].[Payments].[PERCENTAGE_RATE]</refobj> not in (0,null))  then &apos;N&apos; 
else &apos;Y&apos;
end

