case 
when <refobj>[Database Layer].[CONTRACT].[FIXED_PAYMENTS]</refobj>  not in (&apos;&apos;,&apos;NONE&apos;) and  
(<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_VOLUMES]</refobj>  not in (&apos;&apos;,&apos;NONE&apos;) OR
<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_USAGE]</refobj>  not in (&apos;&apos;,&apos;NONE&apos;) OR
<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_REVENUE]</refobj>  not in (&apos;&apos;,&apos;NONE&apos;) OR
<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_BILLING]</refobj>   not in (&apos;&apos;,&apos;NONE&apos;) )  then &apos;FIXED/VARIABLE&apos;
when   <refobj>[Database Layer].[CONTRACT].[FIXED_PAYMENTS]</refobj> not in (&apos;&apos;,&apos;NONE&apos;)  then &apos;FIXED&apos;
when (<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_VOLUMES]</refobj> not in (&apos;&apos;,&apos;NONE&apos;) OR
<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_USAGE]</refobj> not in (&apos;&apos;,&apos;NONE&apos;) OR
<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_REVENUE]</refobj>  not in (&apos;&apos;,&apos;NONE&apos;) OR
<refobj>[Database Layer].[CONTRACT].[VARIABLE_BY_BILLING]</refobj>   not in (&apos;&apos;,&apos;NONE&apos;)  )   then &apos;VARIABLE&apos;
else &apos;PAYMENT TYPE UNDEFINED&apos;
end

